import requests as r
import json
import pytest
import allure

class Test_find():

    @allure.step("发现-精彩推荐")
    def test_recommen(self):
        self.url = 'http://master1.china-plus.net/api/ServiceCenter.do?action=getSpecialList&format=json&verf=ED518E4688BAB7B036DCDEF1E39A79FA&pv=3&data=HQdLXSpWXlZfS1xYGAdLXSpZXl5YS1xYDQdLXSpYXl5fS1xYCh1LXSpbX15WS1xYGABLXSpfQFtAXkBZW1ZLXFgYDUtdKllbVktcWBsHS10qS1xYGx5LXSpLXFgbAEtdKktcW1ssLSNLXFtbKl1aVl1WWlxeX1lLXFtaXh8fQA0BA0tcWBoDS10qIT4%'
        result = r.get(self.url)
        rejson = json.loads(result.text)
        assert rejson['msg']== 'Success'
        assert result.status_code ==200

